import React, { useState } from "react";
import 'C:/Users/юзер/Downloads/diplom/diplom/client/src/components/widgets/widgets.css'

export default function Favorites() {

  const [recommendation, setRecomendetion] = useState(' ');
  const [genre, setGenre] = useState('Rock');
  const [mood, setMood] = useState('Happy');
  const [timeOfDay, setTimeOfDay] = useState('Morning');
  const [activity, setActivity] = useState('Work');
  const [loading, setLoading] = useState(false);
  const fetchRecomend = () =>{
    setLoading(true);
    fetch(`http://localhost:8080/api/suggestions?inputs=${(JSON.stringify({genre, mood, timeOfDay, activity}))}`).then((res)=> res.json()).then((data)=>{
      setRecomendetion(data.aiResponse);
      console.log(recommendation);
      setLoading(false);
    }).catch((error)=>{
      console.error("Error fetch", error);
      setLoading(false);
    })

  }

  return <div className="screen-container">
    <div className="widgets-body">
      <div className="flex-container">
        <div className="song-title"> Recomendation</div>
        <div className="recom">
          <div className="card">
            <label className="label" > activivty: </label>
            <select 
            id="activity"
            value={activity}
            onChange={(e)=>setActivity(e.target.value)}
            className="sel">
              <option value="Working out">Working out</option>
              <option value="Studing">Studing</option>
              <option value="Driving">Driving</option>
              <option value="Relaxing">Relaxing</option>
              <option value="Cooking">Cooking</option>
              <option value="Working">Working</option>
              <option value="Reading">Reading</option>
            </select>
          </div>
          <div className="card">
            <label className="label" > genre: </label>
            <select 
            id="genre"
            value={genre}
            onChange={(e)=>setGenre(e.target.value)}
            className="sel">
            <option value="Pop">Pop</option>
            <option value="Rock">Rock</option>
            <option value="Jazz">Jazz</option>
            <option value="Classical">Classical</option>
            <option value="Hip hop">Hip hop</option>
            <option value="Rap">Rap</option>
          </select>
          </div>

          <div className="card">
            <label className="label" > mood: </label>
            <select 
          id="mood"
          value={mood}
          onChange={(e)=>setMood(e.target.value)}
          className="sel">
            <option value="Happy">Happy</option>
            <option value="Sad">Sad</option>
            <option value="Energitic">Energitic</option>
            <option value="Calm">Calm</option>
            <option value="Romantic">Romantic</option>
            <option value="Motivational">Motivational</option>

          </select>
          </div>

          <div className="card">
            <label className="label" > timeOfDay: </label>
            <select 
          id="timeOfDay"
          value={timeOfDay}
          onChange={(e)=>setTimeOfDay(e.target.value)}
          className="sel">
            <option value="Morning">Morning</option>
            <option value="Day">Day</option>
            <option value="Night">Night</option>

          </select>
          </div>
          <div>
            {loading ? "Loading recomendation..." : recommendation}
          </div>
        </div>
        <div >
          <button onClick={fetchRecomend} className="btn" > Recommend</button>
        </div>
      </div>
    </div>
  </div>;
}
